package main

import (
"sort"
"fmt"
)

func abs(value int)int{
	if value >= 0 {
		return value
	}else{
		return 0 - value
	}
}
func threeSumClosest(nums []int,target int)int {
	sort.Ints(nums)
	index := 0
	begin := 0
	end := 0
	minValue := nums[0] + nums[1] + nums[2] - target
	value := nums[0] + nums[1] + nums[2]
	for index < len(nums){
		begin = index + 1
		end = len(nums) - 1
		for begin < end{
			if nums[index]+ nums[begin] + nums[end] > target{
				if abs(minValue) > abs(nums[index]+ nums[begin] + nums[end] - target){
					minValue = (nums[index]+ nums[begin] + nums[end] - target)
					value = nums[index]+ nums[begin] + nums[end]
					//fmt.Println(nums[index])
				}
				end = end - 1
			}else{
				if nums[index]+ nums[begin] + nums[end] < target{
					if abs(minValue) > abs(nums[index]+ nums[begin] + nums[end] - target){
						minValue = (nums[index]+ nums[begin] + nums[end] - target)
						value = nums[index]+ nums[begin] + nums[end]
						//fmt.Println(nums[index])
					}
					begin = begin + 1
				}else{
					return nums[index]+ nums[begin] + nums[end]
				}
			}
		}
		index++
	}
	return  value
}

